import React from 'react'

export default function Form(props) {

  return (
    <div class="mb-4 container">
        <h1 for="exampleFormControlTextarea1" class="form-label">Enter your Text</h1>
        <textarea class="form-control" value={props.text} id="exampleFormControlTextarea1" rows="8"></textarea>
        <button class="btn btn-primary">Capitalise</button>

    </div>
  )
}
