import React, {useState} from 'react'

export default function Form(props) {
  
    const [text , setText] = useState("Enter Text Here");
    
    const cap = () =>{
        let newText = text.toUpperCase();
        setText(newText);

    }
    const uncap = () =>{
      let newText = text.toLowerCase();
      setText(newText);
    }
    const remove = () =>{
      setText('');
    }  
    const copy = () =>{
      navigator.clipboard.writeText(text);
    } 
    
    const changeText = (event) =>{  
        setText(event.target.value);
    } 
  return (
    <div className="container" >
        <h1 htmlFor="exampleFormControlTextarea1" className="form-label my-4" style={{color:(props.mode==='light'?'black':'white')}}>Enter your Text</h1>
        <textarea className="form-control" value={text} id="exampleFormControlTextarea1" rows="8" onChange={changeText} style={{color:(props.mode==='light'?'grey':'white')}}></textarea>
        <div className='container my-3'>
        <button className="btn btn-primary mx-3" onClick={cap} >Capitalise</button>
        <button className="btn btn-primary mx-3" onClick={uncap} >unCapitalise</button>
        <button className="btn btn-primary mx-3" onClick={remove} >Delete</button>
        <button className="btn btn-primary mx-3" onClick={copy} >copy</button>
        </div>

    </div>
  )
}
