import React, {useState} from 'react'

export default function Form(props) {
  
    const [text , setText] = useState("Enter Text Here");
    
    const cap = () =>{
        let newText = text.toUpperCase();
        setText(newText);

    } 
    
    const changeText = (event) =>{
        setText(newText);
    } 
  return (
    <div class="mb-4 container">
        <h1 for="exampleFormControlTextarea1" class="form-label">Enter your Text</h1>
        <textarea class="form-control" value={text} id="exampleFormControlTextarea1" rows="8"></textarea>
        <button class="btn btn-primary mb-3" onClick={cap} onChange={changeText}>Capitalise</button>

    </div>
  )
}
