import React, {useState} from 'react'

export default function Form(props) {
  
    const [text , setText] = useState("Enter Text Here");
    
    const cap = () =>{
        let newText = text.toUpperCase();
        setText(newText);

    } 
    
    const changeText = (event) =>{  
        setText(event.target.value);
    } 
  return (
    <div className="container">
        <h1 htmlFor="exampleFormControlTextarea1" className="form-label">Enter your Text</h1>
        <textarea className="form-control" value={text} id="exampleFormControlTextarea1" rows="8" onChange={changeText}></textarea>
        <div className='container mb-3 '>
        <button className="btn btn-primary my-3" onClick={cap} >Capitalise</button>
        </div>

    </div>
  )
}
